/**
 * Page Enseignants - Gestion des enseignants
 * Permet de visualiser, ajouter, modifier et supprimer des enseignants
 */

import React, { useState } from 'react';
import { useAppContext } from '../context/AppContext';
import { Plus, Edit, Trash2, Mail, Phone, Book } from 'lucide-react';

// Composants
import Button from '../components/common/Button';
import EnseignantForm from '../components/common/EnseignantForm';

// Styles
import './Enseignants.css';

const Enseignants = () => {
  // Données du context
  const { enseignants, supprimerEnseignant } = useAppContext();

  // États locaux
  const [showModal, setShowModal] = useState(false);
  const [editingEnseignant, setEditingEnseignant] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  // Filtrage
  const filteredEnseignants = enseignants.filter(ens =>
    ens.nom.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ens.prenom.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ens.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    ens.departement.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Actions
  const handleAjouter = () => {
    setEditingEnseignant(null);
    setShowModal(true);
  };

  const handleModifier = (enseignant) => {
    setEditingEnseignant(enseignant);
    setShowModal(true);
  };

  const handleSupprimer = (id) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer cet enseignant ?')) {
      supprimerEnseignant(id);
    }
  };

  const getStatusBadge = (actif) =>
    actif ? 'badge badge-success' : 'badge badge-danger';

  return (
    <div className="enseignants-page">

      {/* En-tête */}
      <div className="page-header">
        <div>
          <h1 className="page-title">Enseignants</h1>
          <p className="page-subtitle">
            Gérez les enseignants et leurs informations
          </p>
        </div>

        <Button
          variant="primary"
          icon={<Plus size={18} />}
          onClick={handleAjouter}
        >
          Ajouter un enseignant
        </Button>
      </div>

      {/* Recherche */}
      <div className="filters-section card">
        <input
          type="text"
          className="form-input"
          placeholder="Rechercher (nom, email, département...)"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Stats */}
      <div className="quick-stats">
        <div className="stat-item">
          <span>Total</span>
          <strong>{enseignants.length}</strong>
        </div>
        <div className="stat-item">
          <span>Actifs</span>
          <strong className="text-success">
            {enseignants.filter(e => e.actif).length}
          </strong>
        </div>
        <div className="stat-item">
          <span>Inactifs</span>
          <strong className="text-danger">
            {enseignants.filter(e => !e.actif).length}
          </strong>
        </div>
      </div>

      {/* Liste */}
      <div className="enseignants-grid">
        {filteredEnseignants.length === 0 ? (
          <p className="empty-state">Aucun enseignant trouvé</p>
        ) : (
          filteredEnseignants.map(ens => (
            <div key={ens.id} className="enseignant-card">

              <div className="card-top">
                <div>
                  <h3>{ens.prenom} {ens.nom}</h3>
                  <span className={getStatusBadge(ens.actif)}>
                    {ens.actif ? 'Actif' : 'Inactif'}
                  </span>
                </div>
                <span className="grade-badge">{ens.grade}</span>
              </div>

              <div className="contact-info">
                <div><Mail size={16} /> {ens.email}</div>
                <div><Phone size={16} /> {ens.telephone}</div>
              </div>

              <div className="departement">
                <span className="badge badge-info">{ens.departement}</span>
              </div>

              <div className="matieres-section">
                <Book size={14} />
                <div className="matieres-list">
                  {ens.matieres.map((m, i) => (
                    <span key={i} className="matiere-tag">{m}</span>
                  ))}
                </div>
              </div>

              <div className="charge-horaire">
                {ens.chargeHoraire}h / semaine
              </div>

              <div className="card-actions">
                <button onClick={() => handleModifier(ens)} className="edit">
                  <Edit size={16} />
                </button>
                <button onClick={() => handleSupprimer(ens.id)} className="delete">
                  <Trash2 size={16} />
                </button>
              </div>

            </div>
          ))
        )}
      </div>

      {/* MODAL */}
      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>

            <div className="modal-header">
              <h2>
                {editingEnseignant
                  ? 'Modifier un enseignant'
                  : 'Ajouter un enseignant'}
              </h2>
              <button className="btn-close" onClick={() => setShowModal(false)}>

              </button>
            </div>

            <div className="modal-body">
              <EnseignantForm
                enseignant={editingEnseignant}
                onClose={() => setShowModal(false)}
              />
            </div>

          </div>
        </div>
      )}

    </div>
  );
};

export default Enseignants;
