import React, { useState } from 'react';
import { useAppContext } from '../../context/AppContext';
import Button from './Button';

const EnseignantForm = ({ enseignant, onClose }) => {
  const { ajouterEnseignant, modifierEnseignant } = useAppContext();

  const [formData, setFormData] = useState({
    prenom: enseignant?.prenom || '',
    nom: enseignant?.nom || '',
    email: enseignant?.email || '',
    telephone: enseignant?.telephone || '',
    departement: enseignant?.departement || '',
    grade: enseignant?.grade || '',
    chargeHoraire: enseignant?.chargeHoraire || 0,
    matieres: enseignant?.matieres?.join(', ') || ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = {
      ...formData,
      matieres: formData.matieres.split(',').map(m => m.trim()),
      actif: true
    };

    if (enseignant) {
      modifierEnseignant(enseignant.id, data);
    } else {
      ajouterEnseignant(data);
    }

    onClose();
  };

  return (
    <form onSubmit={handleSubmit} className="enseignant-form">
      <input name="prenom" value={formData.prenom} onChange={handleChange} placeholder="Prénom" required />
      <input name="nom" value={formData.nom} onChange={handleChange} placeholder="Nom" required />
      <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" />
      <input name="telephone" value={formData.telephone} onChange={handleChange} placeholder="Téléphone" />
      <input name="departement" value={formData.departement} onChange={handleChange} placeholder="Département" />
      <input name="grade" value={formData.grade} onChange={handleChange} placeholder="Grade" />
      <input name="chargeHoraire" type="number" value={formData.chargeHoraire} onChange={handleChange} />
      <input name="matieres" value={formData.matieres} onChange={handleChange} placeholder="Maths, Info, Algo" />

      <div className="form-actions">
        <Button type="submit">
          {enseignant ? 'Modifier' : 'Ajouter'}
        </Button>
        <Button variant="outline" onClick={onClose}>
          Annuler
        </Button>
      </div>
    </form>
  );
};

export default EnseignantForm;
